        timestamp: new Date().toISOString(),
        environment: process.env.NODE_ENV || 'unknown',
        security: {
          httpsEnabled: process.env.HTTPS_ENABLED === 'true',
          authenticationEnabled: process.env.AUTH_ENABLED === 'true',
