        logMessage(log, 'error', 'Error listing users', { error: errorMessage });
        if (error instanceof UserError) {throw error;}
        throw new UserError(`Failed to list users: ${errorMessage}`);
      }
    },
  });

  // Get user details
  server.addTool({
