          count: apps.length,
          total: metadata?.total,
        });

        // Create installation analysis
        const analysis = {
          totalInstalled: metadata?.total || apps.length,
          statusBreakdown: apps.reduce((acc: Record<string, number>, app) => {
            acc[app.status] = (acc[app.status] || 0) + 1;
