          apps: apps.map(app => ({
            ...app,
            installation: {
              ...app.installation,
              configuration: includeConfiguration ? app.installation.configuration : '[CONFIG_HIDDEN]',
            },
          })),
          analysis,
          pagination: {
            total: metadata?.total || apps.length,
            limit,
            offset,
            hasMore: (metadata?.total || 0) > (offset + apps.length),
