      this.emit("service_registered", { serviceId: config.id, config });

      // Perform initial health check
      if (config.enabled && config.healthCheck.enabled) {
        setImmediate(() => this.performHealthCheck(config.id));
      }
    } catch (error) {
      logger.error("Failed to register service", {
        serviceId: config.id,
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  }

  /**
   * Validate service configuration
   */
  private validateServiceConfig(config: ServiceConfig): void {
    if (!config.id || !config.name || !config.type) {
      throw new Error(
        "Service configuration missing required fields: id, name, type",
      );
    }

    if (!config.endpoints || config.endpoints.length === 0) {
      throw new Error("Service configuration must have at least one endpoint");
    }

    for (const endpoint of config.endpoints) {
      if (!endpoint.id || !endpoint.url) {
        throw new Error("Service endpoint missing required fields: id, url");
      }
    }
  }

  /**
   * Unregister a service
   */
  public async unregisterService(serviceId: string): Promise<void> {
    try {
      const config = this.context.services.get(serviceId);
      if (!config) {
        throw new Error(`Service ${serviceId} not found`);
      }

      // Remove from all contexts
      this.context.services.delete(serviceId);
      this.context.healthStatus.delete(serviceId);
      this.context.circuitBreakers.delete(serviceId);
      this.context.connections.delete(serviceId);
      this.context.metrics.delete(serviceId);

      logger.info("Service unregistered successfully", {
        serviceId,
        serviceName: config.name,
      });

      this.emit("service_unregistered", { serviceId, config });
    } catch (error) {
      logger.error("Failed to unregister service", {
        serviceId,
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  }

  /**
   * Execute API request through the integration layer
   */
  public async executeApiRequest(
    context: ApiRequestContext,
    data?: unknown,
  ): Promise<ApiResponseContext> {
    try {
      // Validate request context
      const service = this.context.services.get(context.serviceId);
      if (!service) {
        throw new Error(`Service ${context.serviceId} not found`);
      }

      if (!service.enabled) {
        throw new Error(`Service ${context.serviceId} is disabled`);
      }

      // Check circuit breaker
      const circuitBreakerState = this.context.circuitBreakers.get(
        context.serviceId,
      );
      if (circuitBreakerState && circuitBreakerState.state === "open") {
        throw new Error(
          `Circuit breaker is open for service ${context.serviceId}`,
        );
      }

      // Execute request through worker
      const taskId = this.generateTaskId();
      const workerMessage: IntegrationWorkerMessage = {
        type: "api_request",
        data: {
          context,
          requestData: data,
          serviceConfig: service,
        },
        id: taskId,
        priority: context.priority,
        timeout: context.timeout,
      };

      const response =
        await this.executeWorkerTask<ApiResponseContext>(workerMessage);

      // Update circuit breaker on success
      if (circuitBreakerState) {
        this.updateCircuitBreakerSuccess(context.serviceId);
      }

      // Update metrics
      this.updateServiceMetrics(context.serviceId, {
        successful: true,
        responseTime: response.responseTime,
        timestamp: response.timestamp,
      });

      return response;
    } catch (error) {
      // Update circuit breaker on failure
      const circuitBreakerState = this.context.circuitBreakers.get(
        context.serviceId,
      );
      if (circuitBreakerState) {
        this.updateCircuitBreakerFailure(context.serviceId);
      }

      // Update metrics
      this.updateServiceMetrics(context.serviceId, {
        successful: false,
        error: error instanceof Error ? error.message : String(error),
        timestamp: new Date(),
      });

      logger.error("API request execution failed", {
        serviceId: context.serviceId,
        requestId: context.id,
        error: error instanceof Error ? error.message : String(error),
      });

      throw error;
    }
  }

  /**
   * Execute batch API operations
   */
  public async executeBatchOperation(
    batch: BatchApiOperation,
  ): Promise<ApiResponseContext[]> {
    try {
      const taskId = this.generateTaskId();
      const workerMessage: IntegrationWorkerMessage = {
        type: "batch_operation",
        data: batch,
        id: taskId,
        priority: "normal",
        timeout: batch.timeout,
      };

      const responses =
        await this.executeWorkerTask<ApiResponseContext[]>(workerMessage);

      logger.info("Batch operation completed", {
        batchId: batch.id,
        requestCount: batch.requests.length,
        responseCount: responses.length,
        strategy: batch.strategy,
      });

      this.emit("batch_complete", { batch, responses });
      return responses;
    } catch (error) {
      logger.error("Batch operation failed", {
        batchId: batch.id,
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  }

  /**
   * Get service health status
   */
  public async getServiceHealth(
    serviceId: string,
  ): Promise<ServiceHealthStatus> {
    const healthStatus = this.context.healthStatus.get(serviceId);
    if (!healthStatus) {
      throw new Error(`Service ${serviceId} not found`);
    }

    // Trigger immediate health check if status is old
    const now = new Date();
    const healthAge = now.getTime() - healthStatus.lastChecked.getTime();
    if (healthAge > 60000) {
      // 1 minute
      await this.performHealthCheck(serviceId);
      return this.context.healthStatus.get(serviceId)!;
    }

    return healthStatus;
  }

  /**
   * Get all service health statuses
   */
  public async getAllServiceHealth(): Promise<
    Map<string, ServiceHealthStatus>
  > {
    return new Map(this.context.healthStatus);
  }

  /**
   * Synchronize credentials across services
   */
  public async synchronizeCredentials(
    credentialId: string,
    serviceIds: string[],
  ): Promise<void> {
    try {
      const taskId = this.generateTaskId();
      const workerMessage: IntegrationWorkerMessage = {
        type: "credential_sync",
        data: {
          credentialId,
          serviceIds,
          services: Array.from(this.context.services.entries())
            .filter(([id]) => serviceIds.includes(id))
            .map(([_, config]) => config),
        },
        id: taskId,
        priority: "high",
        timeout: 30000,
      };

      await this.executeWorkerTask(workerMessage);

      logger.info("Credential synchronization completed", {
        credentialId,
        serviceCount: serviceIds.length,
      });

      this.emit("credential_sync", { credentialId, serviceIds });
    } catch (error) {
      logger.error("Credential synchronization failed", {
        credentialId,
        serviceIds,
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  }

  /**
   * Get service metrics
   */
  public async getServiceMetrics(
    serviceId: string,
    timeRange?: { start: Date; end: Date },
  ): Promise<ServiceMetrics[]> {
    const metrics = this.metrics.get(serviceId) || [];

    if (!timeRange) {
      return metrics;
    }

    return metrics.filter(
      (metric) =>
        metric.timestamp >= timeRange.start &&
        metric.timestamp <= timeRange.end,
    );
  }

  /**
   * Get integration agent status
   */
  public async getStatus(): Promise<{
    healthy: boolean;
    services: number;
    activeWorkers: number;
    pendingTasks: number;
    uptime: number;
  }> {
    const healthyWorkers = this.workerPool.filter(
      (worker) => worker.threadId !== undefined,
    ).length;
    const uptime = Date.now() - this.startTime.getTime();

    return {
      healthy:
        this.isInitialized &&
        healthyWorkers >= Math.ceil(this.config.maxWorkers * 0.5),
      services: this.context.services.size,
      activeWorkers: healthyWorkers,
      pendingTasks: this.pendingTasks.size,
      uptime: Math.floor(uptime / 1000), // seconds
    };
  }

  /**
   * Perform health check for a service
   */
  private async performHealthCheck(serviceId: string): Promise<void> {
    try {
      const service = this.context.services.get(serviceId);
      if (!service?.healthCheck.enabled) {
        return;
      }

      const taskId = this.generateTaskId();
      const workerMessage: IntegrationWorkerMessage = {
        type: "health_check",
        data: {
          serviceId,
          serviceConfig: service,
        },
        id: taskId,
        priority: "normal",
        timeout: service.healthCheck.timeoutMs,
      };

      const healthResult =
        await this.executeWorkerTask<ServiceHealthStatus>(workerMessage);

      // Update health status
      const currentHealth = this.context.healthStatus.get(serviceId);
      const oldStatus = currentHealth?.status;

      this.context.healthStatus.set(serviceId, healthResult);

      // Emit health change event if status changed
      if (oldStatus && oldStatus !== healthResult.status) {
        this.emit("service_health_change", {
          type: "service_health_change",
          serviceId,
          oldStatus,
          newStatus: healthResult.status,
          timestamp: new Date(),
          details: healthResult,
        } as IntegrationEvent);
      }
    } catch (error) {
      logger.error("Health check failed", {
        serviceId,
        error: error instanceof Error ? error.message : String(error),
      });

      // Mark service as unhealthy
      const currentHealth = this.context.healthStatus.get(serviceId);
      if (currentHealth) {
        currentHealth.status = "unhealthy";
        currentHealth.consecutiveFailures++;
        currentHealth.consecutiveSuccesses = 0;
        currentHealth.lastChecked = new Date();
      }
    }
  }

  /**
   * Start health monitoring
   */
  private startHealthMonitoring(): void {
    this.healthCheckInterval = setInterval(async () => {
      const services = Array.from(this.context.services.values()).filter(
        (service) => service.enabled && service.healthCheck.enabled,
      );

      // Batch health checks for efficiency
      const batchSize = this.config.healthMonitoring.batchSize;
      for (let i = 0; i < services.length; i += batchSize) {
        const batch = services.slice(i, i + batchSize);

        // Execute health checks in parallel with limited concurrency
        const promises = batch.map((service) =>
          this.performHealthCheck(service.id),
        );
        await Promise.allSettled(promises);
      }
    }, this.config.healthMonitoring.defaultIntervalMs);

    logger.info("Health monitoring started", {
      intervalMs: this.config.healthMonitoring.defaultIntervalMs,
      batchSize: this.config.healthMonitoring.batchSize,
    });
  }

  /**
   * Start metrics collection
   */
  private startMetricsCollection(): void {
    this.metricsInterval = setInterval(() => {
      this.collectServiceMetrics();
    }, this.config.metrics.collectionIntervalMs);

    logger.info("Metrics collection started", {
      intervalMs: this.config.metrics.collectionIntervalMs,
    });
  }

  /**
   * Collect service metrics
   */
  private collectServiceMetrics(): void {
    for (const [serviceId, service] of this.context.services) {
      if (!service.enabled) {
        continue;
      }

      const healthStatus = this.context.healthStatus.get(serviceId);
      const circuitBreaker = this.context.circuitBreakers.get(serviceId);

      const metrics: ServiceMetrics = {
        serviceId,
        timestamp: new Date(),
        requests: {
          total: 0,
          successful: 0,
          failed: 0,
          rate: 0,
        },
        responseTime: {
          mean: healthStatus?.responseTime || 0,
          p50: healthStatus?.responseTime || 0,
          p95: healthStatus?.responseTime || 0,
          p99: healthStatus?.responseTime || 0,
          max: healthStatus?.responseTime || 0,
        },
        errors: {
          total: healthStatus?.consecutiveFailures || 0,
          rate: 0,
          types: {},
        },
        availability: {
          uptime: 0,
          downtime: 0,
          percentage: healthStatus?.status === "healthy" ? 100 : 0,
        },
        circuitBreaker: circuitBreaker
          ? {
              state: circuitBreaker.state,
              failureRate:
                circuitBreaker.requestCount > 0
                  ? (circuitBreaker.failureCount /
                      circuitBreaker.requestCount) *
                    100
                  : 0,
              requestCount: circuitBreaker.requestCount,
            }
          : undefined,
      };

      // Store metrics
      let serviceMetrics = this.metrics.get(serviceId);
      if (!serviceMetrics) {
        serviceMetrics = [];
        this.metrics.set(serviceId, serviceMetrics);
      }

      serviceMetrics.push(metrics);

      // Keep only recent metrics (based on retention period)
      const retentionCutoff = new Date(
        Date.now() - this.config.metrics.retentionPeriodMs,
      );
      const filteredMetrics = serviceMetrics.filter(
        (m) => m.timestamp > retentionCutoff,
      );
      this.metrics.set(serviceId, filteredMetrics);
    }
  }

  /**
   * Update circuit breaker on success
   */
  private updateCircuitBreakerSuccess(serviceId: string): void {
    const state = this.context.circuitBreakers.get(serviceId);
    if (!state) {
      return;
    }

    state.successCount++;
    state.requestCount++;

    if (state.state === "half_open") {
      const service = this.context.services.get(serviceId);
      if (
        service &&
        state.successCount >= service.circuitBreaker.successThreshold
      ) {
        state.state = "closed";
        state.failureCount = 0;
        state.successCount = 0;
        state.lastStateChange = new Date();

        logger.info("Circuit breaker closed", { serviceId });
        this.emit("circuit_breaker_state_change", {
          type: "circuit_breaker_state_change",
          serviceId,
          oldState: "half_open",
          newState: "closed",
          timestamp: new Date(),
          reason: "Success threshold reached",
          details: state,
        } as IntegrationEvent);
      }
    }
  }

  /**
   * Update circuit breaker on failure
   */
  private updateCircuitBreakerFailure(serviceId: string): void {
    const state = this.context.circuitBreakers.get(serviceId);
    if (!state) {
      return;
    }

    const service = this.context.services.get(serviceId);
    if (!service) {
      return;
    }

    state.failureCount++;
    state.requestCount++;

    // Check if we should open the circuit
    if (
      state.state === "closed" &&
      state.requestCount >= service.circuitBreaker.requestVolumeThreshold
    ) {
      const errorRate = (state.failureCount / state.requestCount) * 100;

      if (errorRate >= service.circuitBreaker.errorThresholdPercentage) {
        state.state = "open";
        state.lastStateChange = new Date();
        state.nextEvaluation = new Date(
          Date.now() + service.circuitBreaker.openTimeoutMs,
        );

        logger.warn("Circuit breaker opened", {
          serviceId,
          errorRate: `${errorRate.toFixed(2)}%`,
        });

        this.emit("circuit_breaker_state_change", {
          type: "circuit_breaker_state_change",
          serviceId,
          oldState: "closed",
          newState: "open",
          timestamp: new Date(),
          reason: `Error rate ${errorRate.toFixed(2)}% exceeded threshold`,
          details: state,
        } as IntegrationEvent);
      }
    } else if (state.state === "half_open") {
      state.state = "open";
      state.lastStateChange = new Date();
      state.nextEvaluation = new Date(
        Date.now() + service.circuitBreaker.openTimeoutMs,
      );

      logger.warn("Circuit breaker reopened", { serviceId });
    }
  }

  /**
   * Update service metrics with operation result
   */
  private updateServiceMetrics(
    serviceId: string,
    result: {
      successful: boolean;
      responseTime?: number;
      error?: string;
      timestamp: Date;
    },
  ): void {
    // Store in context metrics for real-time access
    const operationMetrics = {
      timestamp: result.timestamp,
      successful: result.successful,
      responseTime: result.responseTime || 0,
      error: result.error,
    };

    this.context.metrics.set(`${serviceId}_last_operation`, operationMetrics);
  }

  /**
   * Execute a worker task
   */
  private async executeWorkerTask<T = unknown>(
    message: IntegrationWorkerMessage,
  ): Promise<T> {
    return new Promise<T>((resolve, reject) => {
      // Set up timeout
      const timeout = setTimeout(() => {
        this.pendingTasks.delete(message.id);
        reject(new Error(`Worker task timeout after ${message.timeout}ms`));
      }, message.timeout);

      // Store pending task
      this.pendingTasks.set(message.id, { resolve, reject, timeout });

      // Add to queue and process
      this.taskQueue.push(message);
      this.processTaskQueue();
    });
  }

  /**
   * Process worker task queue
   */
  private processTaskQueue(): void {
    if (this.taskQueue.length === 0) {
      return;
    }

    // Find available worker
    const availableWorker = this.workerPool.find(
      (worker) => worker.threadId !== undefined,
    );
    if (availableWorker && this.taskQueue.length > 0) {
      const task = this.taskQueue.shift();
      if (task) {
        availableWorker.postMessage(task);
      }
    }

    // Schedule next processing if there are remaining tasks
    if (this.taskQueue.length > 0) {
      setImmediate(() => this.processTaskQueue());
    }
  }

  /**
   * Generate unique task ID
   */
  private generateTaskId(): string {
    return `task_${Date.now()}_${randomBytes(4).toString("hex")}`;
  }

  /**
   * Shutdown the integration agent
   */
  public async shutdown(): Promise<void> {
    if (this.isShutdown) {
      return;
    }

    this.isShutdown = true;
    logger.info("Shutting down Concurrent Integration Agent");

    try {
      // Clear intervals
      if (this.healthCheckInterval) {
        clearInterval(this.healthCheckInterval);
      }
      if (this.metricsInterval) {
        clearInterval(this.metricsInterval);
      }

      // Clear pending timeouts
      for (const [_taskId, task] of this.pendingTasks) {
        if (task.timeout) {
          clearTimeout(task.timeout);
        }
        task.reject(new Error("Integration agent shutting down"));
      }
      this.pendingTasks.clear();

      // Terminate workers
      await Promise.all(
        this.workerPool.map(async (worker) => {
          try {
            await worker.terminate();
          } catch (error) {
            logger.error("Error terminating worker", { error });
          }
        }),
      );

      this.workers.clear();
      this.workerPool.length = 0;
      this.taskQueue.length = 0;

      logger.info("Concurrent Integration Agent shutdown completed");
      this.emit("shutdown");
    } catch (error) {
      logger.error("Error during integration agent shutdown", {
        error: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  }
}

// Singleton instance
export const concurrentIntegrationAgent = new ConcurrentIntegrationAgent();

export default ConcurrentIntegrationAgent;

/**
 * Enhanced Encryption Service - Integration Layer
 * Bridges existing encryption service with concurrent agents and HSM integration
 */

import { EventEmitter } from "events";
import {
  EncryptionService,
  CredentialManager,
  EncryptedData,
  CryptographicError,
} from "./encryption.js";
import ConcurrentEncryptionAgent from "./concurrent-encryption-agent.js";
import { HSMIntegrationManager } from "./hsm-integration.js";
import {
  EncryptionJobRequest,
  BatchEncryptionRequest,
  ConcurrentWorkerConfig,
  HSMIntegrationConfig,
  CryptographicPerformanceMetrics,
  EncryptionPoolStatus,
  SecurityValidationResult,
} from "../types/encryption-types.js";
import logger from "../lib/logger.js";

export interface EnhancedEncryptionConfig {
  concurrentProcessing: {
    enabled: boolean;
    maxWorkers: number;
    queueSize: number;
    timeout: number;
  };
  hsmIntegration: {
    enabled: boolean;
    config?: HSMIntegrationConfig;
  };
  performanceMonitoring: {
    enabled: boolean;
    metricsRetention: number; // days
    alertThresholds: {
      avgResponseTime: number; // milliseconds
      errorRate: number; // percentage
      throughput: number; // operations per second
    };
  };
  fallbackToSoftware: boolean;
}

export interface EncryptionPerformanceReport {
  timeRange: { start: Date; end: Date };
  totalOperations: number;
  successRate: number;
  avgResponseTime: number;
  peakThroughput: number;
  algorithmBreakdown: Record<
    string,
    {
      operations: number;
      avgTime: number;
      errorRate: number;
    }
  >;
  hsmUsage: {
    enabled: boolean;
    operations: number;
    avgTime: number;
    availability: number;
  };
  recommendations: string[];
}

/**
 * Enhanced Encryption Service
 * Provides unified interface for software and HSM-based cryptographic operations
 */
export class EnhancedEncryptionService extends EventEmitter {
  private readonly baseEncryptionService: EncryptionService;
  private readonly credentialManager: CredentialManager;
  private readonly concurrentAgent?: ConcurrentEncryptionAgent;
  private readonly hsmManager?: HSMIntegrationManager;
  private readonly config: EnhancedEncryptionConfig;
  private readonly componentLogger: ReturnType<typeof logger.child>;

  private performanceMetrics: CryptographicPerformanceMetrics[] = [];
  private isInitialized = false;
  private monitoringInterval?: NodeJS.Timeout;

  constructor(
    config: Partial<EnhancedEncryptionConfig> = {},
    baseEncryptionService?: EncryptionService,
    credentialManager?: CredentialManager,
  ) {
    super();

    this.config = {
      concurrentProcessing: {
        enabled: config.concurrentProcessing?.enabled ?? true,
        maxWorkers: config.concurrentProcessing?.maxWorkers ?? 4,
        queueSize: config.concurrentProcessing?.queueSize ?? 1000,
        timeout: config.concurrentProcessing?.timeout ?? 30000,
      },
      hsmIntegration: {
        enabled: config.hsmIntegration?.enabled ?? false,
        config: config.hsmIntegration?.config,
      },
      performanceMonitoring: {
        enabled: config.performanceMonitoring?.enabled ?? true,
        metricsRetention: config.performanceMonitoring?.metricsRetention ?? 30,
        alertThresholds: {
          avgResponseTime:
            config.performanceMonitoring?.alertThresholds?.avgResponseTime ??
            1000,
          errorRate:
            config.performanceMonitoring?.alertThresholds?.errorRate ?? 5,
          throughput:
            config.performanceMonitoring?.alertThresholds?.throughput ?? 10,
          ...config.performanceMonitoring?.alertThresholds,
        },
      },
      fallbackToSoftware: config.fallbackToSoftware ?? true,
    };

    this.baseEncryptionService =
      baseEncryptionService || new EncryptionService();
    this.credentialManager = credentialManager || new CredentialManager();
    this.componentLogger = logger.child({
      component: "EnhancedEncryptionService",
    });

    // Initialize concurrent processing if enabled
    if (this.config.concurrentProcessing.enabled) {
      const workerConfig: ConcurrentWorkerConfig = {
        maxWorkers: this.config.concurrentProcessing.maxWorkers,
        queueSize: this.config.concurrentProcessing.queueSize,
        workerTimeout: this.config.concurrentProcessing.timeout,
        resourceLimits: {
          maxOldGenerationSizeMb: 128,
          maxYoungGenerationSizeMb: 64,
        },
        isolatedContext: true,
      };

      this.concurrentAgent = new ConcurrentEncryptionAgent(
        workerConfig,
        this.config.hsmIntegration.config,
      );
    }

    // Initialize HSM integration if enabled
    if (
      this.config.hsmIntegration.enabled &&
      this.config.hsmIntegration.config
    ) {
      this.hsmManager = new HSMIntegrationManager(
        this.config.hsmIntegration.config,
      );
    }
  }

  /**
   * Initialize the enhanced encryption service
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) {
      throw new Error("EnhancedEncryptionService already initialized");
    }

    try {
      this.componentLogger.info("Initializing enhanced encryption service", {
        concurrentProcessing: this.config.concurrentProcessing.enabled,
        hsmIntegration: this.config.hsmIntegration.enabled,
        performanceMonitoring: this.config.performanceMonitoring.enabled,
      });

      // Initialize concurrent agent if enabled
      if (this.concurrentAgent) {
        await this.concurrentAgent.initialize();

        // Setup event handlers
        this.concurrentAgent.on("jobCompleted", (result) => {
          this.emit("operationCompleted", result);
          if (result.metadata) {
            this.recordPerformanceMetric({
              operationType: "concurrent_job",
              algorithm: result.metadata.algorithm,
              dataSize: 0, // Would need to track from original request
              processingTime: result.metadata.processingTime,
              throughput: 1000 / result.metadata.processingTime,
              cpuUsage: 0,
              memoryUsage: 0,
              workerId: result.metadata.workerId,
              timestamp: new Date(),
              hsm: result.metadata.hsm,
            });
          }
        });

        this.concurrentAgent.on("jobError", (result) => {
          this.emit("operationError", result);
        });
      }

      // Initialize HSM manager if enabled
      if (this.hsmManager) {
        await this.hsmManager.initialize();

        // Setup HSM event handlers
        this.hsmManager.on("providerConnected", (provider) => {
          this.componentLogger.info("HSM provider connected", { provider });
          this.emit("hsmConnected", provider);
        });

        this.hsmManager.on("providerError", (provider, error) => {
          this.componentLogger.error("HSM provider error", { provider, error });
          this.emit("hsmError", provider, error);
        });
      }

      // Start performance monitoring if enabled
      if (this.config.performanceMonitoring.enabled) {
        this.startPerformanceMonitoring();
      }

      this.isInitialized = true;
      this.componentLogger.info(
        "Enhanced encryption service initialized successfully",
      );
      this.emit("initialized");
    } catch (error) {
      this.componentLogger.error(
        "Failed to initialize enhanced encryption service",
        {
          error: error instanceof Error ? error.message : "Unknown error",
        },
      );
      throw error;
    }
  }

  /**
   * Enhanced encryption with automatic HSM/concurrent processing selection
   */
  async encrypt(
    plaintext: string,
    masterPassword: string,
    options: {
      useHSM?: boolean;
      useConcurrent?: boolean;
      algorithm?: string;
      priority?: "low" | "medium" | "high" | "critical";
    } = {},
  ): Promise<EncryptedData> {
    if (!this.isInitialized) {
      throw new Error("EnhancedEncryptionService not initialized");
    }

    const startTime = Date.now();

    try {
      // Determine processing method
      const useHSM = options.useHSM && !!this.hsmManager;
      const useConcurrent =
        options.useConcurrent && !!this.concurrentAgent && !useHSM;

      let result: EncryptedData;

      if (useHSM) {
        // Use HSM for encryption
        result = await this.encryptWithHSM(
          plaintext,
          masterPassword,
          options.algorithm,
        );
      } else if (useConcurrent) {
        // Use concurrent agent for encryption
        result = await this.encryptWithConcurrentAgent(
          plaintext,
          masterPassword,
          options,
        );
      } else {
        // Use base encryption service
        result = await this.baseEncryptionService.encrypt(
          plaintext,
          masterPassword,
        );
      }

      // Record performance metrics
      this.recordPerformanceMetric({
        operationType: "encrypt",
        algorithm: result.algorithm,
        dataSize: plaintext.length,
        processingTime: Date.now() - startTime,
        throughput: plaintext.length / ((Date.now() - startTime) / 1000),
        cpuUsage: process.cpuUsage().user / 1000000,
        memoryUsage: process.memoryUsage().heapUsed / 1024 / 1024,
        workerId: useHSM ? "hsm" : useConcurrent ? "concurrent" : "software",
        timestamp: new Date(),
        hsm: useHSM,
      });

      return result;
    } catch (error) {
      // Fallback to software implementation if configured
      if (
        this.config.fallbackToSoftware &&
        (options.useHSM || options.useConcurrent)
      ) {
        this.componentLogger.warn("Falling back to software encryption", {
          originalError:
            error instanceof Error ? error.message : "Unknown error",
        });
        return await this.baseEncryptionService.encrypt(
          plaintext,
          masterPassword,
        );
      }

      throw error;
    }
  }

  /**
   * Enhanced decryption with automatic HSM/concurrent processing selection
   */
  async decrypt(
    encryptedData: EncryptedData,
    masterPassword: string,
    options: {
      useHSM?: boolean;
      useConcurrent?: boolean;
      priority?: "low" | "medium" | "high" | "critical";
    } = {},
  ): Promise<string> {
    if (!this.isInitialized) {
      throw new Error("EnhancedEncryptionService not initialized");
    }

    const startTime = Date.now();

    try {
      // Determine processing method
      const useHSM = options.useHSM && !!this.hsmManager;
      const useConcurrent =
        options.useConcurrent && !!this.concurrentAgent && !useHSM;

      let result: string;

      if (useHSM) {
        // Use HSM for decryption
        result = await this.decryptWithHSM(encryptedData, masterPassword);
      } else if (useConcurrent) {
        // Use concurrent agent for decryption
        result = await this.decryptWithConcurrentAgent(
          encryptedData,
          masterPassword,
          options,
        );
      } else {
        // Use base encryption service
        result = await this.baseEncryptionService.decrypt(
          encryptedData,
          masterPassword,
        );
      }

      // Record performance metrics
      this.recordPerformanceMetric({
        operationType: "decrypt",
        algorithm: encryptedData.algorithm,
        dataSize: encryptedData.data.length,
        processingTime: Date.now() - startTime,
        throughput: result.length / ((Date.now() - startTime) / 1000),
        cpuUsage: process.cpuUsage().user / 1000000,
        memoryUsage: process.memoryUsage().heapUsed / 1024 / 1024,
        workerId: useHSM ? "hsm" : useConcurrent ? "concurrent" : "software",
        timestamp: new Date(),
        hsm: useHSM,
      });

      return result;
    } catch (error) {
      // Fallback to software implementation if configured
      if (
        this.config.fallbackToSoftware &&
        (options.useHSM || options.useConcurrent)
      ) {
        this.componentLogger.warn("Falling back to software decryption", {
          originalError:
            error instanceof Error ? error.message : "Unknown error",
        });
        return await this.baseEncryptionService.decrypt(
          encryptedData,
          masterPassword,
        );
      }

      throw error;
    }
  }

  /**
   * Batch encryption with concurrent processing
   */
  async encryptBatch(
    requests: Array<{
      plaintext: string;
      masterPassword: string;
      id: string;
      priority?: "low" | "medium" | "high" | "critical";
    }>,
    options: {
      maxConcurrency?: number;
      timeout?: number;
      failFast?: boolean;
    } = {},
  ): Promise<
    Array<{
      id: string;
      success: boolean;
      result?: EncryptedData;
      error?: string;
    }>
  > {
    if (!this.concurrentAgent) {
      throw new Error("Concurrent processing not enabled");
    }

    const jobs: EncryptionJobRequest[] = requests.map((req) => ({
      id: req.id,
      operation: "encrypt",
      algorithm: {
        algorithm: "aes-256-gcm",
        keyLength: 256,
        ivLength: 16,
        tagLength: 16,
      },
      data: req.plaintext,
      key: req.masterPassword,
      metadata: {
        priority: req.priority || "medium",
      },
    }));

    const batchRequest: BatchEncryptionRequest = {
      batchId: crypto.randomUUID(),
      jobs,
      options: {
        maxConcurrency:
          options.maxConcurrency || this.config.concurrentProcessing.maxWorkers,
        timeout: options.timeout || this.config.concurrentProcessing.timeout,
        failFast: options.failFast || false,
      },
    };

    const batchResult = await this.concurrentAgent.processBatch(batchRequest);

    return batchResult.results.map((result) => ({
      id: result.id,
      success: result.success,
      result:
        result.success && result.result
          ? (result.result as EncryptedData)
          : undefined,
      error: result.error?.message,
    }));
  }

  /**
   * Generate cryptographically secure key pairs with HSM support
   */
  async generateKeyPair(
    algorithm: "rsa-4096" | "ecdsa-p384" | "ed25519",
    options: {
      useHSM?: boolean;
      extractable?: boolean;
      usage?: string[];
    } = {},
  ): Promise<{
    publicKey: string;
    privateKey?: string;
    keyId: string;
    hsmBacked: boolean;
  }> {
    if (options.useHSM && this.hsmManager) {
      // Use HSM for key generation
      const keySpec = {
        keyId: crypto.randomUUID(),
        keyType: "asymmetric" as const,
        algorithm,
        keyLength: this.getKeyLength(algorithm),
        extractable: options.extractable || false,
        usage: options.usage || ["encrypt", "decrypt", "sign", "verify"],
      };

      const result = await this.hsmManager.generateKey(keySpec);

      if (result.success && result.keyId) {
        return {
          publicKey: result.result as string,
          keyId: result.keyId,
          hsmBacked: true,
        };
      } else {
        throw new CryptographicError(
          result.error?.message || "HSM key generation failed",
          "generateKeyPair",
        );
      }
    } else if (this.concurrentAgent) {
      // Use concurrent agent for key generation
      const result = await this.concurrentAgent.generateKeyPair(
        algorithm,
        options,
      );

      return {
        publicKey: result.publicKey,
        privateKey: result.privateKey,
        keyId: result.keyId,
        hsmBacked: false,
      };
    } else {
      // Fallback to Node.js crypto
      const keyPair = (crypto as any).generateKeyPairSync(
        algorithm === "rsa-4096"
          ? "rsa"
          : algorithm === "ecdsa-p384"
            ? "ec"
            : "ed25519",
        algorithm === "rsa-4096"
          ? {
              modulusLength: 4096,
              publicKeyEncoding: { type: "spki", format: "pem" },
              privateKeyEncoding: { type: "pkcs8", format: "pem" },
            }
          : algorithm === "ecdsa-p384"
            ? {
                namedCurve: "secp384r1",
                publicKeyEncoding: { type: "spki", format: "pem" },
                privateKeyEncoding: { type: "pkcs8", format: "pem" },
              }
            : {
                publicKeyEncoding: { type: "spki", format: "pem" },
                privateKeyEncoding: { type: "pkcs8", format: "pem" },
              },
      );

      return {
        publicKey: keyPair.publicKey as string,
        privateKey: options.extractable
          ? (keyPair.privateKey as string)
          : undefined,
        keyId: crypto.randomUUID(),
        hsmBacked: false,
      };
    }
  }

  /**
   * Get comprehensive performance report
   */
  getPerformanceReport(timeRange?: {
    start: Date;
    end: Date;
  }): EncryptionPerformanceReport {
    const now = new Date();
    const start =
      timeRange?.start || new Date(now.getTime() - 24 * 60 * 60 * 1000); // Last 24 hours
    const end = timeRange?.end || now;

    const relevantMetrics = this.performanceMetrics.filter(
      (metric) => metric.timestamp >= start && metric.timestamp <= end,
    );

    if (relevantMetrics.length === 0) {
      return {
        timeRange: { start, end },
        totalOperations: 0,
        successRate: 100,
        avgResponseTime: 0,
        peakThroughput: 0,
        algorithmBreakdown: {},
        hsmUsage: {
          enabled: !!this.hsmManager,
          operations: 0,
          avgTime: 0,
          availability: 0,
        },
        recommendations: ["No operations recorded in the specified time range"],
      };
    }

    const totalOperations = relevantMetrics.length;
    const avgResponseTime =
      relevantMetrics.reduce((sum, m) => sum + m.processingTime, 0) /
      totalOperations;
    const peakThroughput = Math.max(
      ...relevantMetrics.map((m) => m.throughput),
    );

    // Algorithm breakdown
    const algorithmBreakdown: Record<string, any> = {};
    for (const metric of relevantMetrics) {
      if (!algorithmBreakdown[metric.algorithm]) {
        algorithmBreakdown[metric.algorithm] = {
          operations: 0,
          totalTime: 0,
          avgTime: 0,
          errorRate: 0,
        };
      }
      algorithmBreakdown[metric.algorithm].operations++;
      algorithmBreakdown[metric.algorithm].totalTime += metric.processingTime;
    }

    for (const alg in algorithmBreakdown) {
      const data = algorithmBreakdown[alg];
      data.avgTime = data.totalTime / data.operations;
      delete data.totalTime;
    }

    // HSM usage
    const hsmMetrics = relevantMetrics.filter((m) => m.hsm);
    const hsmUsage = {
      enabled: !!this.hsmManager,
      operations: hsmMetrics.length,
      avgTime:
        hsmMetrics.length > 0
          ? hsmMetrics.reduce((sum, m) => sum + m.processingTime, 0) /
            hsmMetrics.length
          : 0,
      availability: this.hsmManager ? 100 : 0, // Simplified - would need actual health checks
    };

    // Generate recommendations
    const recommendations: string[] = [];
    if (
      avgResponseTime >
      this.config.performanceMonitoring.alertThresholds.avgResponseTime
    ) {
      recommendations.push(
        "Average response time is above threshold. Consider enabling concurrent processing or HSM acceleration.",
      );
    }
    if (
      peakThroughput <
      this.config.performanceMonitoring.alertThresholds.throughput
    ) {
      recommendations.push(
        "Throughput is below optimal levels. Consider increasing worker count or optimizing key derivation parameters.",
      );
    }
    if (hsmUsage.enabled && hsmUsage.operations / totalOperations < 0.5) {
      recommendations.push(
        "HSM is available but underutilized. Consider routing more operations through HSM for enhanced security.",
      );
    }

    return {
      timeRange: { start, end },
      totalOperations,
      successRate: 100, // Simplified - would need actual error tracking
      avgResponseTime,
      peakThroughput,
      algorithmBreakdown,
      hsmUsage,
      recommendations,
    };
  }

  /**
   * Get encryption pool status (concurrent agent)
   */
  getPoolStatus(): EncryptionPoolStatus | null {
    return this.concurrentAgent?.getPoolStatus() || null;
  }

  /**
   * Validate encryption configuration security
   */
  validateSecurity(): SecurityValidationResult {
    const validations = {
      keyStrength: true, // Always use strong keys
      algorithmCompliance: true, // Use FIPS-compliant algorithms
      randomnessQuality: true, // Node.js crypto provides secure randomness
      timingAttackResistance: !!this.concurrentAgent, // Concurrent processing provides some protection
      sideChannelResistance: !!this.hsmManager, // HSM provides side-channel resistance
    };

    const score =
      Object.values(validations).filter((v) => v).length /
      Object.values(validations).length;

    let securityLevel: SecurityValidationResult["securityLevel"];
    if (score >= 0.9 && this.hsmManager) {
      securityLevel = "fips-140-2";
    } else if (score >= 0.8) {
      securityLevel = "high";
    } else if (score >= 0.6) {
      securityLevel = "medium";
    } else {
      securityLevel = "low";
    }

    const recommendations: string[] = [];
    if (!this.hsmManager) {
      recommendations.push(
        "Enable HSM integration for enhanced security and FIPS 140-2 compliance",
      );
    }
    if (!this.concurrentAgent) {
      recommendations.push(
        "Enable concurrent processing for improved performance and timing attack resistance",
      );
    }

    return {
      isValid: score >= 0.8,
      securityLevel,
      validations,
      recommendations: recommendations.length > 0 ? recommendations : undefined,
    };
  }

  /**
   * Shutdown the enhanced encryption service
   */
  async shutdown(): Promise<void> {
    try {
      this.componentLogger.info("Shutting down enhanced encryption service");

      if (this.monitoringInterval) {
        clearInterval(this.monitoringInterval);
      }

      if (this.concurrentAgent) {
        await this.concurrentAgent.shutdown();
      }

      if (this.hsmManager) {
        await this.hsmManager.shutdown();
      }

      this.isInitialized = false;
      this.componentLogger.info(
        "Enhanced encryption service shutdown completed",
      );
      this.emit("shutdown");
    } catch (error) {
      this.componentLogger.error("Error during shutdown", {
        error: error instanceof Error ? error.message : "Unknown error",
      });
      throw error;
    }
  }

  // Private helper methods

  private async encryptWithHSM(
    plaintext: string,
    masterPassword: string,
    algorithm?: string,
  ): Promise<EncryptedData> {
    if (!this.hsmManager) {
      throw new Error("HSM manager not initialized");
    }

    // For simplicity, use a predefined HSM key or derive one
    const keyId = "master-encryption-key"; // This would be managed differently in production
    const plaintextBuffer = Buffer.from(plaintext, "utf8");

    const result = await this.hsmManager.encrypt(
      keyId,
      plaintextBuffer,
      algorithm,
    );

    if (!result.success || !result.result) {
      throw new CryptographicError(
        result.error?.message || "HSM encryption failed",
        "encryptWithHSM",
      );
    }

    // Convert HSM result to EncryptedData format
    return {
      data: (result.result as Buffer).toString("base64"),
      iv: crypto.randomBytes(16).toString("base64"), // HSM should provide this
      salt: crypto.randomBytes(32).toString("base64"), // HSM should provide this
      algorithm: algorithm || "aes-256-gcm-hsm",
      keyLength: 256,
    };
  }

  private async decryptWithHSM(
    encryptedData: EncryptedData,
    _masterPassword: string,
  ): Promise<string> {
    if (!this.hsmManager) {
      throw new Error("HSM manager not initialized");
    }

    const keyId = "master-encryption-key";
    const ciphertextBuffer = Buffer.from(encryptedData.data, "base64");

    const result = await this.hsmManager.decrypt(
      keyId,
      ciphertextBuffer,
      encryptedData.algorithm,
    );

    if (!result.success || !result.result) {
      throw new CryptographicError(
        result.error?.message || "HSM decryption failed",
        "decryptWithHSM",
      );
    }

    return (result.result as Buffer).toString("utf8");
  }

  private async encryptWithConcurrentAgent(
    plaintext: string,
    masterPassword: string,
    options: { priority?: "low" | "medium" | "high" | "critical" },
  ): Promise<EncryptedData> {
    if (!this.concurrentAgent) {
      throw new Error("Concurrent agent not initialized");
    }

    const jobRequest: EncryptionJobRequest = {
      id: crypto.randomUUID(),
      operation: "encrypt",
      algorithm: {
        algorithm: "aes-256-gcm",
        keyLength: 256,
        ivLength: 16,
        tagLength: 16,
      },
      data: plaintext,
      key: masterPassword,
      metadata: {
        priority: options.priority || "medium",
      },
    };

    const result = await this.concurrentAgent.processJob(jobRequest);

    if (!result.success || !result.result) {
      throw new CryptographicError(
        result.error?.message || "Concurrent encryption failed",
        "encryptWithConcurrentAgent",
      );
    }

    return result.result as EncryptedData;
  }

  private async decryptWithConcurrentAgent(
    encryptedData: EncryptedData,
    masterPassword: string,
    options: { priority?: "low" | "medium" | "high" | "critical" },
  ): Promise<string> {
    if (!this.concurrentAgent) {
      throw new Error("Concurrent agent not initialized");
    }

    const jobRequest: EncryptionJobRequest = {
      id: crypto.randomUUID(),
      operation: "decrypt",
      algorithm: {
        algorithm: encryptedData.algorithm as any,
        keyLength: encryptedData.keyLength,
      },
      data: encryptedData,
      key: masterPassword,
      metadata: {
        priority: options.priority || "medium",
      },
    };

    const result = await this.concurrentAgent.processJob(jobRequest);

    if (!result.success || !result.result) {
      throw new CryptographicError(
        result.error?.message || "Concurrent decryption failed",
        "decryptWithConcurrentAgent",
      );
    }

    return result.result as string;
  }

  private recordPerformanceMetric(
    metric: CryptographicPerformanceMetrics,
  ): void {
    this.performanceMetrics.push(metric);

    // Keep only metrics within retention period
    const retentionMs =
      this.config.performanceMonitoring.metricsRetention * 24 * 60 * 60 * 1000;
    const cutoff = new Date(Date.now() - retentionMs);
    this.performanceMetrics = this.performanceMetrics.filter(
      (m) => m.timestamp >= cutoff,
    );
  }

  private startPerformanceMonitoring(): void {
    this.monitoringInterval = setInterval(() => {
      this.checkPerformanceThresholds();
    }, 60000); // Every minute
  }

  private checkPerformanceThresholds(): void {
    const recent = this.performanceMetrics.slice(-100); // Last 100 operations
    if (recent.length === 0) {
      return;
    }

    const avgResponseTime =
      recent.reduce((sum, m) => sum + m.processingTime, 0) / recent.length;
    const avgThroughput =
      recent.reduce((sum, m) => sum + m.throughput, 0) / recent.length;

    if (
      avgResponseTime >
      this.config.performanceMonitoring.alertThresholds.avgResponseTime
    ) {
      this.emit("performanceAlert", {
        type: "high_response_time",
        value: avgResponseTime,
        threshold:
          this.config.performanceMonitoring.alertThresholds.avgResponseTime,
      });
    }

    if (
      avgThroughput <
      this.config.performanceMonitoring.alertThresholds.throughput
    ) {
      this.emit("performanceAlert", {
        type: "low_throughput",
        value: avgThroughput,
        threshold: this.config.performanceMonitoring.alertThresholds.throughput,
      });
    }
  }

  private getKeyLength(algorithm: string): number {
    const keyLengths: Record<string, number> = {
      "rsa-4096": 4096,
      "ecdsa-p384": 384,
      ed25519: 256,
    };
    return keyLengths[algorithm] || 256;
  }
}

export default EnhancedEncryptionService;

/**
 * Error recovery mechanisms and retry logic for Make.com FastMCP Server
 * Implements circuit breaker patterns, exponential backoff, and resilience strategies
 */

import { randomUUID } from "crypto";
import {
  MakeServerError,
  UserError,
  createExternalServiceError,
  createTimeoutError,
  getErrorStatusCode,
  getErrorCode,
} from "./errors.js";
import logger from "../lib/logger.js";
import { ComponentLogger } from "../types/logger.js";

export interface RetryOptions {
  maxRetries?: number;
  baseDelay?: number;
  maxDelay?: number;
  exponentialBase?: number;
  jitter?: boolean;
  retryCondition?: (error: Error) => boolean;
  onRetry?: (error: Error, attempt: number) => void;
}

export interface CircuitBreakerOptions {
  failureThreshold?: number;
  successThreshold?: number;
  timeout?: number;
  resetTimeout?: number;
  onStateChange?: (state: CircuitBreakerState) => void;
}

export type CircuitBreakerState = "CLOSED" | "OPEN" | "HALF_OPEN";

/**
 * Circuit Breaker implementation for external service calls
 */
export class CircuitBreaker {
  private state: CircuitBreakerState = "CLOSED";
  private failureCount = 0;
  private successCount = 0;
  private nextAttempt = 0;
  private readonly options: Required<CircuitBreakerOptions>;
  private readonly componentLogger: ComponentLogger;

  constructor(
    private readonly name: string,
    options: CircuitBreakerOptions = {},
  ) {
    this.options = {
      failureThreshold: options.failureThreshold ?? 5,
      successThreshold: options.successThreshold ?? 3,
      timeout: options.timeout ?? 30000,
      resetTimeout: options.resetTimeout ?? 60000,
      onStateChange: options.onStateChange ?? ((): void => {}),
    };

    this.componentLogger = logger.child({
      component: "CircuitBreaker",
      circuitName: name,
    });
  }

  async execute<T>(
    operation: () => Promise<T>,
    correlationId?: string,
  ): Promise<T> {
    const operationLogger = this.componentLogger.child({
      correlationId: correlationId || randomUUID(),
      operation: "circuit-breaker-execute",
    });

    if (this.state === "OPEN") {
      if (Date.now() < this.nextAttempt) {
        const error = createExternalServiceError(
          this.name,
          "Circuit breaker is OPEN - requests blocked",
          undefined,
          {
            circuitState: this.state,
            nextAttempt: new Date(this.nextAttempt).toISOString(),
            failureCount: this.failureCount,
          },
          {
            correlationId,
            component: "CircuitBreaker",
            operation: "circuit-breaker-execute",
          },
        );

        operationLogger.warn("Circuit breaker blocking request", {
          state: this.state,
          nextAttempt: new Date(this.nextAttempt).toISOString(),
        });

        throw error;
      } else {
        this.setState("HALF_OPEN");
        operationLogger.info("Circuit breaker transitioning to HALF_OPEN");
      }
    }

    try {
      const startTime = Date.now();
      const result = await Promise.race([
        operation(),
        this.createTimeoutPromise<T>(),
      ]);

      const duration = Date.now() - startTime;
      this.onSuccess();

      operationLogger.info("Circuit breaker operation succeeded", {
        duration,
        state: this.state,
      });

      return result;
    } catch (error) {
      this.onFailure();

      operationLogger.error("Circuit breaker operation failed", {
        error: error instanceof Error ? error.message : String(error),
        state: this.state,
        failureCount: this.failureCount,
      });

      throw error;
    }
  }

  private createTimeoutPromise<T>(): Promise<T> {
    return new Promise((_, reject) => {
      setTimeout(() => {
        reject(
          createTimeoutError(
            `Circuit breaker operation for ${this.name}`,
            this.options.timeout,
          ),
        );
      }, this.options.timeout);
    });
  }

  private onSuccess(): void {
    this.failureCount = 0;

    if (this.state === "HALF_OPEN") {
      this.successCount++;
      if (this.successCount >= this.options.successThreshold) {
        this.setState("CLOSED");
        this.successCount = 0;
      }
    }
  }

  private onFailure(): void {
    this.failureCount++;
    this.successCount = 0;

    if (this.failureCount >= this.options.failureThreshold) {
      this.setState("OPEN");
      this.nextAttempt = Date.now() + this.options.resetTimeout;
    }
  }

  private setState(newState: CircuitBreakerState): void {
    const oldState = this.state;
    this.state = newState;

    this.componentLogger.info("Circuit breaker state changed", {
      oldState,
      newState,
      failureCount: this.failureCount,
      successCount: this.successCount,
    });

    this.options.onStateChange(newState);
  }

  public getState(): CircuitBreakerState {
    return this.state;
  }

  public getStats(): {
    state: CircuitBreakerState;
    failureCount: number;
    successCount: number;
    nextAttempt: number;
  } {
    return {
      state: this.state,
      failureCount: this.failureCount,
      successCount: this.successCount,
      nextAttempt: this.nextAttempt,
    };
  }
}

/**
 * Retry mechanism with exponential backoff and jitter
 */
export async function retryWithBackoff<T>(
  operation: () => Promise<T>,
  options: RetryOptions = {},
  correlationId?: string,
): Promise<T> {
  const {
    maxRetries = 3,
    baseDelay = 1000,
    maxDelay = 30000,
    exponentialBase = 2,
    jitter = true,
    retryCondition = defaultRetryCondition,
    onRetry,
  } = options;

  const getOperationLogger = (): ComponentLogger => {
    try {
      return logger.child({
        component: "RetryMechanism",
        correlationId: correlationId || randomUUID(),
        operation: "retry-with-backoff",
      }) as ComponentLogger;
    } catch {
      // Fallback for test environments with proper typing
      return {
        debug: (..._args: unknown[]): void => {
          /* fallback debug */
        },
        info: (..._args: unknown[]): void => {
          /* fallback info */
        },
        warn: (...args: unknown[]): void => console.warn(...args),
        error: (...args: unknown[]): void => console.error(...args),
        child: (_options: Record<string, unknown>) => getOperationLogger(),
      };
    }
  };
  const operationLogger = getOperationLogger();

  let lastError: Error = new Error("No attempts made");

  for (let attempt = 0; attempt <= maxRetries; attempt++) {
    try {
      const result = await operation();

      if (attempt > 0) {
        operationLogger.info("Operation succeeded after retries", {
          attempt,
          totalAttempts: attempt + 1,
        });
      }

      return result;
    } catch (error) {
      lastError = error as Error;

      operationLogger.warn("Operation failed, evaluating retry", {
        attempt,
        error: lastError.message,
        isLastAttempt: attempt === maxRetries,
      });

      // Don't retry on the last attempt or if retry condition is not met
      if (attempt === maxRetries || !retryCondition(lastError)) {
        break;
      }

      // Calculate delay with exponential backoff and optional jitter
      const delay = Math.min(
        baseDelay * Math.pow(exponentialBase, attempt),
        maxDelay,
      );

      const finalDelay = jitter ? delay + Math.random() * 1000 : delay;

      operationLogger.info("Retrying operation after delay", {
        attempt: attempt + 1,
        delay: finalDelay,
        nextAttempt: attempt + 1,
      });

      // Call retry callback if provided
      if (onRetry) {
        try {
          onRetry(lastError, attempt + 1);
        } catch (callbackError) {
          operationLogger.warn("Retry callback failed", {
            error:
              callbackError instanceof Error
                ? callbackError.message
                : String(callbackError),
          });
        }
      }

      await new Promise((resolve) => setTimeout(resolve, finalDelay));
    }
  }

  // All retries exhausted, throw the last error
  operationLogger.error("All retry attempts exhausted", {
    totalAttempts: maxRetries + 1,
    finalError: lastError.message,
  });

  throw lastError;
}

/**
 * Default retry condition - retries on network and timeout errors
 * Now compatible with UserError and maintains backward compatibility
 */
export function defaultRetryCondition(error: Error): boolean {
  // Retry on network errors
  if ("code" in error) {
    const networkErrors = [
      "ECONNRESET",
      "ECONNREFUSED",
      "ETIMEDOUT",
      "ENOTFOUND",
    ];
    if (networkErrors.includes(error.code as string)) {
      return true;
    }
  }

  // Get status code using helper function
  const statusCode = getErrorStatusCode(error);
  const errorCode = getErrorCode(error);

  // Retry on specific HTTP status codes for all error types
  if (statusCode) {
    const retryableStatusCodes = [408, 429, 500, 502, 503, 504];
    if (retryableStatusCodes.includes(statusCode)) {
      return true;
    }
  }

  // Retry on timeout errors (by error code)
  if (errorCode === "TIMEOUT") {
    return true;
  }

  // Retry on external service errors
  if (errorCode === "EXTERNAL_SERVICE_ERROR") {
    return true;
  }

  // Don't retry on client errors (4xx) or specific server errors
  if (error instanceof MakeServerError) {
    return error.statusCode >= 500 && error.statusCode !== 501;
  }

  // For UserError, check status code
  if (error instanceof UserError) {
    return statusCode >= 500 && statusCode !== 501;
  }

  return false;
}

/**
 * Bulkhead pattern implementation for resource isolation
 */
export class Bulkhead {
  private activeRequests = 0;
  private readonly queue: Array<{
    operation: () => Promise<unknown>;
    resolve: (value: unknown) => void;
    reject: (error: Error) => void;
    correlationId: string;
  }> = [];

  private readonly componentLogger: ComponentLogger;

  constructor(
    private readonly name: string,
    private readonly maxConcurrency: number = 10,
    private readonly maxQueue: number = 100,
    private readonly timeout: number = 30000,
  ) {
    this.componentLogger = logger.child({
      component: "Bulkhead",
      bulkheadName: name,
    });
  }

  async execute<T>(
    operation: () => Promise<T>,
    correlationId?: string,
  ): Promise<T> {
    const requestId = correlationId || randomUUID();
    const operationLogger = this.componentLogger.child({
      correlationId: requestId,
      operation: "bulkhead-execute",
    });

    return new Promise<T>((resolve, reject) => {
      if (this.activeRequests < this.maxConcurrency) {
        this.executeImmediate(operation, resolve, reject, requestId);
      } else if (this.queue.length < this.maxQueue) {
        operationLogger.info("Request queued due to bulkhead limit", {
          activeRequests: this.activeRequests,
          queueLength: this.queue.length,
        });

        this.queue.push({
          operation: operation as () => Promise<unknown>,
          resolve: resolve as (value: unknown) => void,
          reject,
          correlationId: requestId,
        });
      } else {
        const error = createExternalServiceError(
          this.name,
          "Bulkhead capacity exceeded - request rejected",
          undefined,
          {
            activeRequests: this.activeRequests,
            queueLength: this.queue.length,
            maxConcurrency: this.maxConcurrency,
            maxQueue: this.maxQueue,
          },
          {
            correlationId: requestId,
            component: "Bulkhead",
            operation: "bulkhead-execute",
          },
        );

        operationLogger.error("Bulkhead capacity exceeded", {
          activeRequests: this.activeRequests,
          queueLength: this.queue.length,
        });

        reject(error);
      }
    });
  }

  private async executeImmediate<T>(
    operation: () => Promise<T>,
    resolve: (value: T) => void,
    reject: (error: Error) => void,
    correlationId: string,
  ): Promise<void> {
    this.activeRequests++;

    const operationLogger = this.componentLogger.child({
      correlationId,
      operation: "bulkhead-execute-immediate",
    });

    const timeoutId = setTimeout(() => {
      const timeoutError = createTimeoutError(
        `Bulkhead operation for ${this.name}`,
        this.timeout,
      );
      reject(timeoutError);
    }, this.timeout);

    try {
      const result = await operation();
      clearTimeout(timeoutId);
      resolve(result);

      operationLogger.info("Bulkhead operation completed successfully", {
        activeRequests: this.activeRequests - 1,
      });
    } catch (error) {
      clearTimeout(timeoutId);
      reject(error as Error);

      operationLogger.error("Bulkhead operation failed", {
        error: error instanceof Error ? error.message : String(error),
        activeRequests: this.activeRequests - 1,
      });
    } finally {
      this.activeRequests--;
      this.processQueue();
    }
  }

  private processQueue(): void {
    if (this.queue.length > 0 && this.activeRequests < this.maxConcurrency) {
      const next = this.queue.shift();
      if (next) {
        this.executeImmediate(
          next.operation as () => Promise<unknown>,
          next.resolve,
          next.reject,
          next.correlationId,
        );
      }
    }
  }

  public getStats(): {
    activeRequests: number;
    queueLength: number;
    maxConcurrency: number;
    maxQueue: number;
  } {
    return {
      activeRequests: this.activeRequests,
      queueLength: this.queue.length,
      maxConcurrency: this.maxConcurrency,
      maxQueue: this.maxQueue,
    };
  }
}

/**
 * Factory for creating circuit breakers for different services
 */
export class CircuitBreakerFactory {
  private static readonly breakers = new Map<string, CircuitBreaker>();

  static getOrCreate(
    name: string,
    options?: CircuitBreakerOptions,
  ): CircuitBreaker {
    if (!this.breakers.has(name)) {
      this.breakers.set(name, new CircuitBreaker(name, options));
    }
    const breaker = this.breakers.get(name);
    if (!breaker) {
      throw new Error(`Failed to create or retrieve circuit breaker: ${name}`);
    }
    return breaker;
  }

  static getAllStats(): Record<string, ReturnType<CircuitBreaker["getStats"]>> {
    const stats: Record<string, ReturnType<CircuitBreaker["getStats"]>> = {};
    for (const [name, breaker] of this.breakers) {
      stats[name] = breaker.getStats();
    }
    return stats;
  }
}

/**
 * Factory for creating bulkheads for different resource pools
 */
export class BulkheadFactory {
  private static readonly bulkheads = new Map<string, Bulkhead>();

  static getOrCreate(
    name: string,
    maxConcurrency?: number,
    maxQueue?: number,
    timeout?: number,
  ): Bulkhead {
    if (!this.bulkheads.has(name)) {
      this.bulkheads.set(
        name,
        new Bulkhead(name, maxConcurrency, maxQueue, timeout),
      );
    }
    const bulkhead = this.bulkheads.get(name);
    if (!bulkhead) {
      throw new Error(`Failed to create or retrieve bulkhead: ${name}`);
    }
    return bulkhead;
  }

  static getAllStats(): Record<string, ReturnType<Bulkhead["getStats"]>> {
    const stats: Record<string, ReturnType<Bulkhead["getStats"]>> = {};
    for (const [name, bulkhead] of this.bulkheads) {
      stats[name] = bulkhead.getStats();
    }
    return stats;
  }
}

/**
 * Hardware Security Module (HSM) Integration
 * Enterprise-grade key management and cryptographic operations with HSM backends
 */

import { EventEmitter } from "events";
import * as crypto from "crypto";
import {
  HSMIntegrationConfig,
  HSMProvider,
  CryptographicAuditLog,
} from "../types/encryption-types.js";
import logger from "../lib/logger.js";

export interface HSMKeySpec {
  keyId: string;
  keyType: "symmetric" | "asymmetric" | "derivation";
  algorithm: string;
  keyLength: number;
  extractable: boolean;
  usage: string[];
  attributes?: Record<string, unknown>;
}

export interface HSMOperationResult {
  success: boolean;
  result?: Buffer | string;
  keyId?: string;
  metadata?: {
    operationType: string;
    timestamp: Date;
    provider: HSMProvider;
    performance: {
      duration: number;
      throughput?: number;
    };
  };
  error?: {
    code: string;
    message: string;
    recoverable: boolean;
  };
}

export interface HSMStatus {
  provider: HSMProvider;
  connected: boolean;
  authenticated: boolean;
  keySlots: {
    total: number;
    used: number;
    available: number;
  };
  performance: {
    avgResponseTime: number;
    operationsPerSecond: number;
    errorRate: number;
  };
  lastHealthCheck: Date;
  firmwareVersion?: string;
  serialNumber?: string;
}

/**
 * Abstract HSM Provider Interface
 */
abstract class HSMProvider_Abstract extends EventEmitter {
  protected config: HSMIntegrationConfig;
  protected componentLogger: ReturnType<typeof logger.child>;
  protected isConnected = false;
  protected isAuthenticated = false;
  protected keyCache: Map<string, HSMKeySpec> = new Map();
  protected operationMetrics: Array<{
    timestamp: Date;
    operation: string;
    duration: number;
    success: boolean;
  }> = [];

  constructor(config: HSMIntegrationConfig) {
    super();
    this.config = config;
    this.componentLogger = logger.child({
      component: "HSMProvider",
      provider: config.provider,
    });
  }

  abstract connect(): Promise<void>;
  abstract authenticate(): Promise<void>;
  abstract disconnect(): Promise<void>;
  abstract generateKey(spec: HSMKeySpec): Promise<HSMOperationResult>;
  abstract importKey(
    keyData: Buffer,
    spec: HSMKeySpec,
  ): Promise<HSMOperationResult>;
  abstract exportKey(
    keyId: string,
    wrappingKeyId?: string,
  ): Promise<HSMOperationResult>;
  abstract deleteKey(keyId: string): Promise<HSMOperationResult>;
  abstract encrypt(
    keyId: string,
    plaintext: Buffer,
    algorithm?: string,
  ): Promise<HSMOperationResult>;
  abstract decrypt(
    keyId: string,
    ciphertext: Buffer,
    algorithm?: string,
  ): Promise<HSMOperationResult>;
  abstract sign(
    keyId: string,
    data: Buffer,
    algorithm?: string,
  ): Promise<HSMOperationResult>;
  abstract verify(
    keyId: string,
    data: Buffer,
    signature: Buffer,
    algorithm?: string,
  ): Promise<HSMOperationResult>;
  abstract getKeyInfo(keyId: string): Promise<HSMKeySpec | null>;
  abstract listKeys(): Promise<HSMKeySpec[]>;
  abstract getStatus(): Promise<HSMStatus>;

  protected recordMetric(
    operation: string,
    duration: number,
    success: boolean,
  ): void {
    this.operationMetrics.push({
      timestamp: new Date(),
      operation,
      duration,
      success,
    });

    // Keep only last 10000 metrics
    if (this.operationMetrics.length > 10000) {
      this.operationMetrics = this.operationMetrics.slice(-10000);
    }
  }

  protected getAverageResponseTime(): number {
    if (this.operationMetrics.length === 0) {
      return 0;
    }

    const recentMetrics = this.operationMetrics.slice(-1000); // Last 1000 operations
    const totalTime = recentMetrics.reduce(
      (sum, metric) => sum + metric.duration,
      0,
    );
    return totalTime / recentMetrics.length;
  }

  protected getOperationsPerSecond(): number {
    const recentMetrics = this.operationMetrics.slice(-1000);
    if (recentMetrics.length < 2) {
      return 0;
    }

    const timeSpan =
      recentMetrics[recentMetrics.length - 1].timestamp.getTime() -
      recentMetrics[0].timestamp.getTime();

    return timeSpan > 0 ? (recentMetrics.length / timeSpan) * 1000 : 0;
  }

  protected getErrorRate(): number {
    if (this.operationMetrics.length === 0) {
      return 0;
    }

    const recentMetrics = this.operationMetrics.slice(-1000);
    const errorCount = recentMetrics.filter((metric) => !metric.success).length;
    return (errorCount / recentMetrics.length) * 100;
  }
}

